import React, { useState } from "react";
import axios from "axios";

function App() {
  const [userQuery, setUserQuery] = useState("");
  const [recommendations, setRecommendations] = useState([]);

  // If both containers run via docker-compose:
  // Use the container name for the backend ("anime_backend") plus port 8000.
  const baseURL = "http://anime_backend:8000/anime-recommendations";

  // If you run locally (no Docker), use "http://127.0.0.1:8000/anime-recommendations";

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(baseURL, { userQuery });
      setRecommendations(response.data.recommendedAnimeTitles || []);
    } catch (error) {
      console.error("Error in POST request:", error);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>Anime Recommendation App</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={userQuery}
          onChange={(e) => setUserQuery(e.target.value)}
          placeholder="Enter anime you like"
        />
        <button type="submit">Get Recommendations</button>
      </form>

      <div style={{ marginTop: "20px" }}>
        <h2>Recommendations:</h2>
        <ul>
          {recommendations.map((anime, index) => (
            <li key={index}>{anime}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
